document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll(".swiper").forEach((function(el){new Swiper(el,{loop:!0,pagination:{el:el.querySelector(".swiper-pagination"),clickable:!0},keyboard:{enabled:!0},freeMode:!0,navigation:{nextEl:el.querySelector(".swiper-button-next"),prevEl:el.querySelector(".swiper-button-prev")}})}))}));